var pwdSchema = require('./index');
var schema = new pwdSchema().uppercase().has().digits().isMax(9).isMin(4);

console.log(schema.validate('qwerty'));
console.log(schema.validate('ALLCAPS00'));
console.log(schema.validate('ALLCAPS'));
console.log(schema.validate('lowerbutoolong'));
console.log(schema.validate());